import React, {useState, useEffect} from 'react';
import { Text, View, Pressable} from 'react-native';
import MaterialCommunityIcons from'@expo/vector-icons/MaterialCommunityIcons';
import styles from '../style/style';
import { Col, Grid, Row } from "react-native-easy-grid";

let board = [];
const NBR_OF_DICES = 5;
const NBR_OF_THROWS = 3;
let number = [];
let number2 = [];
let number3 = [];
let number4 = [];
let number5 = [];
let number6 = [];

export default function Gameboard() {
    const [nbrOfThrowsLeft, setNbrOfThrowsLeft] = useState(NBR_OF_THROWS);
    const [status, setStatus] = useState('');
    const [selectedDices, setSelectedDices] = useState(new Array (NBR_OF_DICES).fill(false));
    const [selectedIcons, setSelectedIcons] = useState(new Array (6).fill(false));
    const [total, setTotal] = useState(0);
    const [points1, setPoints1] = useState(0);
    const [points2, setPoints2] = useState(0);
    const [points3, setPoints3] = useState(0);
    const [points4, setPoints4] = useState(0);
    const [points5, setPoints5] = useState(0);
    const [points6, setPoints6] = useState(0);

    const row = [];
    for (let i = 0; i < NBR_OF_DICES; i++) {
        row.push(
            <Pressable key={"row" + i} onPress={() => selectDice(i)}>
                <MaterialCommunityIcons
                    name={board[i]}
                    key={"row" + i}
                    size={50}
                    color={getDiceColor(i)}>
                </MaterialCommunityIcons>
            </Pressable>
        );
    }

    const numberIcon = [];
    for (let n = 1; n < 7; n++) {
        numberIcon.push(
            <View>
                <Pressable key={"icon" + n} onPress={() => selectIcons(n)} style={styles.icon}>
                    <MaterialCommunityIcons
                        name={getIcon(n)}
                        key={"icon" + n}
                        size={40}
                        color="#6b705c">
                    </MaterialCommunityIcons>
                </Pressable>
            </View> 
        );
    }

    useEffect(() => {
        checkWinner();
        if (nbrOfThrowsLeft === NBR_OF_THROWS) {
            setStatus('Throw dices.');
        }
        if (nbrOfThrowsLeft < 0) {
            setNbrOfThrowsLeft(NBR_OF_THROWS-1);
        }
    }, [nbrOfThrowsLeft]);

    function getDiceColor(i) {
            return selectedDices[i] ? "#cb997e" : "#b7b7a4";
        }

    function getIcon(n) {
        return selectedIcons[n] ? "numeric-" + n + "-circle" : "numeric-" + n + "-circle-outline";
    }

    function selectDice(i) {
        let dices = [...selectedDices];
        dices[i] = selectedDices[i] ? false : true;
        setSelectedDices(dices);

    }

    function selectIcons(n) {
        let icons = [...selectedIcons];
        icons[n] = selectedIcons[n] ? false : true;
        setSelectedIcons(icons);
        setSelectedDices(new Array (NBR_OF_DICES).fill(false));
        calculate();
        }

    function throwDices() {
        for (let i = 0; i < NBR_OF_DICES; i++) {
            if (!selectedDices[i]) {
                let randomNumber = Math.floor(Math.random() * 6 + 1);
                board[i] = 'dice-' + randomNumber;
                if (board[i] === 'dice-1') {
                    number[i] = 1;
                } else if (board[i] === 'dice-2') {
                    number2[i] = 2;
                } else if (board[i] === 'dice-3') {
                    number3[i] = 3;
                } else if (board[i] === 'dice-4') {
                    number4[i] = 4;
                } else if (board[i] === 'dice-5') {
                    number5[i] = 5;
                } else if (board[i] === 'dice-6') {
                    number6[i] = 6;
                }
            }
        }
        setNbrOfThrowsLeft(nbrOfThrowsLeft-1);
    }

    function checkWinner() {
        if (nbrOfThrowsLeft === 0) {
            setStatus('Select your points.');
        } else if (nbrOfThrowsLeft < 0) {
            setNbrOfThrowsLeft(NBR_OF_THROWS-1);
            setSelectedDices(new Array (NBR_OF_DICES).fill(false));
        } else {
            setStatus('Select and throw dices again.');
    }}

    function calculate() {
        let sum = 0;
        if (selectedIcons[1] === true) {
            sum = number.reduce((a, b) => a + b, 0);
            setPoints1(sum);
        }
        if (selectedIcons[2] === true) {
            sum = number2.reduce((a, b) => a + b, 0);
            setPoints2(sum);
        }
        if (selectedIcons[3] === true) {
            sum = number3.reduce(function(a, b) { return a + b; }, 0);
            setPoints3(sum);
        }
        if (selectedIcons[4] === true) {
            sum = number4.reduce(function(a, b) { return a + b; }, 0);
            setPoints4(sum);
        }
        if (selectedIcons[5] === true) {
            sum = number5.reduce(function(a, b) { return a + b; }, 0);
            setPoints5(sum);
        }
        if (selectedIcons[6] === true) {
            sum = number6.reduce(function(a, b) { return a + b; }, 0);
            setPoints6(sum);
        }
    }

    return(
        <View style={styles.gameboard}>
            <View style={styles.flex}>{row}</View>
            <Text style={styles.gameinfo}>Throws left: {nbrOfThrowsLeft}</Text>
            <Text style={styles.gameinfo}>{status}</Text>
            <Pressable style={styles.button} onPress={() => throwDices()}>
                <Text style={styles.buttonText}>
                    Throw dices
                </Text>
            </Pressable>
            <Text style={styles.total}>Total: {total}</Text>
            <View style={styles.points}>
                <Col style={{width: 45}}>
                    <Text style={styles.zahl}>{points1}</Text>
                    <View style={styles.icon}>{numberIcon[0]}</View>
                </Col>
                <Col style={{width: 45}}>
                    <Text style={styles.zahl}>{points2}</Text>
                    <View style={styles.icon}>{numberIcon[1]}</View>
                </Col>
                <Col style={{width: 45}}>
                    <Text style={styles.zahl}>{points3}</Text>
                    <View style={styles.icon}>{numberIcon[2]}</View>
                </Col>
                <Col style={{width: 45}}>
                    <Text style={styles.zahl}>{points4}</Text>
                    <View style={styles.icon}>{numberIcon[3]}</View>
                </Col>
                <Col style={{width: 45}}>
                    <Text style={styles.zahl}>{points5}</Text>
                    <View style={styles.icon}>{numberIcon[4]}</View>
                </Col>
                <Col style={{width: 45}}>
                    <Text style={styles.zahl}>{points6}</Text>
                    <View style={styles.icon}>{numberIcon[5]}</View>
                </Col>
            </View>
        </View>
    )
}
